/**
 * Generated by `createservice billing.SearchBillingAccountsWithoutConsumerService --type queries`
 */

const { GQLError, GQLErrorCode: { BAD_USER_INPUT } } = require('@open-condo/keystone/errors')
const { GQLCustomSchema } = require('@open-condo/keystone/schema')

const access = require('@condo/domains/billing/access/SearchBillingAccountsWithoutConsumerService')
const { BillingAccount } = require('@condo/domains/billing/utils/serverSchema')
const { NOT_UNIQUE } = require('@condo/domains/common/constants/errors')
const { loadListByChunks } = require('@condo/domains/common/utils/serverSchema')
const { CONTEXT_FINISHED_STATUS } = require('@condo/domains/miniapp/constants')
const { Property } = require('@condo/domains/property/utils/serverSchema')
const { Resident, ServiceConsumer } = require('@condo/domains/resident/utils/serverSchema')

/**
 * List of possible errors, that this custom schema can throw
 * They will be rendered in documentation section in GraphiQL for this custom schema
 */
const errors = {
    DUPLICATED_RESIDENTS: {
        mutation: 'searchBillingAccountsWithoutConsumer',
        variable: ['data', 'residents'],
        code: BAD_USER_INPUT,
        type: NOT_UNIQUE,
        message: 'There are resident duplication',
        messageForUser: 'api.billing.searchBillingAccountsWithoutConsumer.DUPLICATED_RESIDENTS',
    },
    MISSING_RESIDENTS: {
        mutation: 'searchBillingAccountsWithoutConsumer',
        variable: ['data', 'residents'],
        code: BAD_USER_INPUT,
        type: NOT_UNIQUE,
        message: 'Can not find some residents',
        messageForUser: 'api.billing.searchBillingAccountsWithoutConsumer.MISSING_RESIDENTS',
    },
    MISSING_RESIDENT_PROPERTY: {
        mutation: 'searchBillingAccountsWithoutConsumer',
        code: BAD_USER_INPUT,
        type: NOT_UNIQUE,
        message: 'Can not find some properties for specified residents',
        messageForUser: 'api.billing.searchBillingAccountsWithoutConsumer.MISSING_RESIDENT_PROPERTY',
    },
}

const SearchBillingAccountsWithoutConsumerService = new GQLCustomSchema('SearchBillingAccountsWithoutConsumerService', {
    types: [
        {
            access: true,
            type: 'input SearchBillingAccountsWithoutConsumerInput { residents: [ResidentWhereUniqueInput!]! }',
        },
        {
            access: true,
            type: 'type SearchBillingAccountsWithoutConsumerAccountsGroup { resident: Resident, accounts: [BillingAccount!]! }',
        },
        {
            access: true,
            type: 'type SearchBillingAccountsWithoutConsumerOutput { residentsAccounts: [SearchBillingAccountsWithoutConsumerAccountsGroup!]! }',
        },
    ],
    
    queries: [
        {
            access: access.canSearchBillingAccountsWithoutConsumer,
            schema: 'searchBillingAccountsWithoutConsumer (data: SearchBillingAccountsWithoutConsumerInput!): SearchBillingAccountsWithoutConsumerOutput',
            resolver: async (parent, args, context, info, extra = {}) => {
                const { data } = args
                const {
                    residents: residentIds,
                } = data

                const uniqueResidentIds = new Set(residentIds.map(resident => resident.id))
                if (residentIds.length !== uniqueResidentIds.size) {
                    throw new GQLError(errors.DUPLICATED_RESIDENTS, context)
                }

                // firstly try to retrieve all residents passed to the service
                const residents = await loadListByChunks({
                    context,
                    list: Resident,
                    where: {
                        id_in: uniqueResidentIds,
                        user: { id: context.authedItem.id },
                        deletedAt: null,
                    },
                })

                if (residents.length !== residentIds.length) {
                    throw new GQLError(errors.MISSING_RESIDENTS, context)
                }

                // retrieve related ServiceConsumers
                const consumers = await loadListByChunks({
                    context,
                    list: ServiceConsumer,
                    where: {
                        resident: { id_in: uniqueResidentIds },
                        deletedAt: null,
                    },
                })

                // filter residents that have service consumer
                const residentsWithoutConsumer = residents.filter(resident => {
                    const exists = consumers.find(consumer => consumer.resident.id === resident.id)
                    return !exists
                })

                // we have to proceed search resident by resident
                const residentsAccounts = []
                for (let i = 0 ; i < residentsWithoutConsumer.length; i++) {
                    const {
                        unitName,
                        unitType,
                        property: {
                            id: propertyId,
                        },
                    } = residentsWithoutConsumer[i]

                    const property = await Property.getOne(context, { id: propertyId, deletedAt: null }, {
                        doesNotExistError: errors.MISSING_RESIDENT_PROPERTY,
                    })

                    // assemble conditions
                    const addressCondition = {
                        OR: [
                            { property: { address_i: property.address } },
                            { property: { addressKey: property.addressKey } },
                            { property: { normalizedAddress_i: property.address } },
                        ],
                    }
                    const unitCondition = {
                        unitName,
                        unitType,
                    }
                    const integrationCondition = {
                        context: {
                            status: CONTEXT_FINISHED_STATUS,
                        },
                    }

                    const accounts = await loadListByChunks({
                        context,
                        list: BillingAccount,
                        where: {
                            ...addressCondition,
                            ...unitCondition,
                            ...integrationCondition,
                            deletedAt: null,
                        },
                    })

                    residentsAccounts.push({
                        resident: residents[i],
                        accounts,
                    })
                }

                return {
                    residentsAccounts,
                }
            },
        },
    ],
    
})

module.exports = {
    SearchBillingAccountsWithoutConsumerService,
}
